case3.code:
static string HandleRoute(string path)
{
    var segments = path.Trim('/').Split('/', StringSplitOptions.RemoveEmptyEntries);
    return segments switch
    {
        ["products"] => "ListProducts",
        ["products", "new"] => "CreateProduct",
        ["products", var id] => $"GetProduct({id})",
        ["users", "me"] => "GetCurrentUser",
        ["users", var id, "orders", .. var rest] => $"UserOrders({id})/{string.Join('/', rest)}",
        [] => "Home",
        _ => "NotFound"
    };
}

