unidiff.code:
using DiffPlex;
using DiffPlex.DiffBuilder;
using DiffPlex.DiffBuilder.Model;
using DiffPlex.Chunkers;
using DiffPlex.Unidiff;

string text1 = oldText;
string text2 = newText;

// simple
string uni = UnidiffRenderer.GenerateUnidiff(text1, text2, "before.txt", "after.txt");

// with options (context lines, ignore case/whitespace, chunker)
var renderer = new UnidiffRenderer(contextLines: 3);
string uni2 = renderer.Generate(text1, text2, "before.txt", "after.txt");

threeWay.code:
using DiffPlex;
using DiffPlex.Chunkers;

var threeWay = new ThreeWayDiffer();
var diff = threeWay.CreateDiffs(baseText, oldText, newText,
    ignoreWhiteSpace: false, ignoreCase: false, new LineChunker());

var merge = threeWay.CreateMerge(baseText, oldText, newText,
    ignoreWhiteSpace: false, ignoreCase: false, new LineChunker());

if (merge.HasConflicts)
{
    Console.WriteLine($"Conflicts: {merge.ConflictBlocks.Count}");
}
else
{
    Console.WriteLine(merge.MergedText);
}
